import React, { useState, useEffect, useRef } from "react";
import useScrollSnap from "react-use-scroll-snap";

import './App.scss';
import Splash from './components/Splash';
import About from './components/About';
import MainWorks from './components/MainWorks';
import SideWorks from './components/SideWorks';
import CTA from './components/CTA';

function App() {
  const worksRef = useRef();
  const aboutRef = useRef();
  const splashRef = useRef();
  const [scrollPos, setScrollPos] = useState( 0 );
  const [isNotFirstPage, setIsNotFirstPage] = useState( false );

  useEffect( () => {
    function onScroll() {
      let currentPosition = window.pageYOffset; // or use document.documentElement.scrollTop;
      if ( currentPosition > 500 ) {
        setIsNotFirstPage( true );
      }
      else {
        setIsNotFirstPage( false );
      }
      setScrollPos( currentPosition );
    }
    window.addEventListener( "scroll", onScroll );
    return () => window.removeEventListener( "scroll", onScroll );
  }, [scrollPos] );

  function scrollTo( toGo ) {
    toGo.current.scrollIntoView( { behavior: 'smooth' } );
  }

  return (
    <div className="App">
      <span ref={splashRef}>
        <Splash />
      </span>
      <span ref={aboutRef}>
        <About />
      </span>

      <span ref={worksRef}>
        <MainWorks title='Escapee' category='Video Game • RPG • Unity' images={[1, 2, 3]} dark={false} ytLink="https://www.youtube.com/embed/f3OJeqz19LE" />
      </span>
      <MainWorks title='Liminal Floor' category='Study • Horror • React.js' images={[7, 8, 9]} dark={false} ytLink="https://www.youtube.com/embed/oDc_kKkF0g0" />
      {/* <MainWorks title='Flower Magic' category='Video Game • Visual Novel • Unity' images={[5, 6]} dark={true} /> */}
      <MainWorks title='Water Monopoly' category='Board Game • Speculative Design • Adobe Illustrator' images={[11, 12, 13]} dark={false} />
      <MainWorks title='Jane Austen' category='Website • VR • Next.js' images={[14, 15, 16]} dark={true} />
      <MainWorks title='SALBOY FIFTY5IVE' category='Website • VR • JQuery' images={[17, 18, 19]} dark={true} />

      <SideWorks title='Marketing' category='AIESEC, Dimsum Culture Platform' images={[11, 12, 13]} />
      <SideWorks title='Graphic' category='VA works, ISSIA' images={[11, 12, 13]} />

      <CTA isNotFirstPage={isNotFirstPage} aboutRef={aboutRef} worksRef={worksRef} scrollTo={scrollTo} />
      {isNotFirstPage &&
        <a className='scrollTopbtn' >
          <img width="50" height="50" src="https://img.icons8.com/ios/50/000000/long-arrow-up--v1.png" alt="long-arrow-up--v1" />
        </a>
      }
    </div>
  );
}

export default App;
