import { useRef } from "react";
import useScrollSnap from "react-use-scroll-snap";

import './App.css';
import Splash from './components/Splash';
import About from './components/About';
import MainWorks from './components/MainWorks';
import SideWorks from './components/SideWorks';
import CTA from './components/CTA';

function App() {
  const scrollRef = useRef( null );
  useScrollSnap( { ref: scrollRef, duration: 0, delay: 0 } );
  return (
    <div className="App" ref={scrollRef}>
      <Splash />
      <About />

      <MainWorks title='Escapee' category='Video Game • RPG • Unity' images={[1, 2, 3]} dark={false} ytLink="https://www.youtube.com/embed/f3OJeqz19LE" />
      <MainWorks title='Liminal Floor' category='Study • Horror • React.js' images={[7, 8, 9]} dark={false} ytLink="https://www.youtube.com/embed/oDc_kKkF0g0" />
      <MainWorks title='Flower Magic' category='Video Game • Visual Novel • Unity' images={[5, 6]} dark={true} />
      <MainWorks title='Water Monopoly' category='Board Game • Speculative Design • Adobe Illustrator' images={[11, 12, 13]} dark={false} />
      <MainWorks title='Jane Austen' category='Website • VR • Next.js' images={[14, 15, 16]} dark={true} />
      <MainWorks title='SALBOY FIFTY5IVE' category='Website • VR • JQuery' images={[17, 18, 19]} dark={true} />

      <SideWorks title='Marketing' category='AIESEC, Dimsum Culture Platform' images={[11, 12, 13]} />
      <SideWorks title='Graphic' category='VA works, ISSIA' images={[11, 12, 13]} />

      <CTA />
    </div>
  );
}

export default App;
