import './App.css';
import { useRef } from "react";
import useScrollSnap from "react-use-scroll-snap";

import Splash from './components/Splash';
import About from './components/About';
import MainWorks from './components/MainWorks';
import SideWorks from './components/SideWorks';
import CTA from './components/CTA';


function App() {
  const scrollRef = useRef( null );
  useScrollSnap( { ref: scrollRef, duration: 50, delay: 20 } );
  return (
    <div className="App">
      <Splash />
      <About />

      <MainWorks title='I need to Escapee' category='Video Game • RPG • Unity' />
      <MainWorks title='Flower Magic' category='Video Game • Visual Novel • Unity' />
      <MainWorks title='Liminal Floor' category='Study • Horror • React.js' />
      <MainWorks title='Water Monopoly' category='Board Game • Speculative Design • Adobe Illustrator' />
      <MainWorks title='Jane Austen' category='Website • VR • Next.js' />
      <MainWorks title='SALBOY FIFTY5IVE' category='Website • VR • JQuery' />

      <SideWorks title='3D Modelling' category='Recursive tree, Camera study' />
      <SideWorks title='Marketing' category='AIESEC, Dimsum Culture Platform' />
      <SideWorks title='Graphic' category='VA works, ISSIA' />
      <SideWorks title='Video Editing' category='Youtube' />

      <CTA />
    </div>
  );
}

export default App;
