import React, { useEffect, useRef } from 'react'

const About = () => {
    const headLineRef = useRef();
    useEffect( () => {
        gsap.fromTo(
            headLineRef.current,
            { autoAlpha: 0, y: -40 },
            {
                autoAlpha: 1, y: 0, duration: 1,
                scrollTrigger: {
                    scroller: 'html',
                    trigger: headLineRef.current,
                    start: "top 60%",
                    end: "bottom 0%",
                    toggleActions: "play none restart reverse"
                },
            },
        );
    }, [] );
    return (
        <section className='aboutContainer' ref={headLineRef}>
            <h2>Educated at the University of Hong Kong under BASc Design+ and Computer Science.</h2>
            <h2>I specialise in</h2>
            <h2 id='aboutSubcaption'>But I'm also good at</h2>
        </section>
    )
}

export default About
