import React, { useState, useRef } from 'react'
// Import Swiper React components
import { Swiper, SwiperSlide, useSwiper } from 'swiper/react';
import { EffectFade } from 'swiper';

// Import Swiper styles
import 'swiper/css';
import 'swiper/css/effect-fade';

const MainWorks = ( { title, category, images, dark } ) => {
    const swiper = useSwiper();
    const [my_swiper, set_my_swiper] = useState( {} );

    return (
        <section className='mainGalleryContainer'>
            <span className='mainWorksTitles'>
                <h1>{title}</h1>
                <h2>{category}</h2>
            </span>
            <Swiper
                pagination={{
                    dynamicBullets: true,
                }}
                modules={[EffectFade]}
                effect="fade"
                className="mySwiper"
                onInit={( ev ) => {
                    set_my_swiper( ev )
                }}
                onClick={() => {
                    console.log( "hmm" );
                    my_swiper.slideNext();
                }}
                loop={true}
            >
                {images.map( ( slideContent ) => (
                    <SwiperSlide key={slideContent}>
                        {/* <img src="./MainWorks/1.jpg" alt={slideContent} /> */}
                        <img className={dark ? 'arrCursorDark' : 'arrCursorLight'} src={`../MainWorks/${slideContent}.jpg`} alt={slideContent} />
                    </SwiperSlide>
                ) )}
            </Swiper>
        </section>
    )
}

export default MainWorks
