import React, { useState } from 'react'
// Import Swiper React components
import { Swiper, SwiperSlide } from 'swiper/react';
import { EffectFade } from 'swiper';

// Import Swiper styles
import 'swiper/css';
import 'swiper/css/effect-fade';

const SideWorks = ( { title, category, images, dark } ) => {
    const [my_swiper, set_my_swiper] = useState( {} );

    return (
        <section className='sideGalleryContainer'>
            <span className='mainWorksTitles'>
                <h1>{title}</h1>
                <h2>{category}</h2>
            </span>
            <Swiper
                pagination={{
                    dynamicBullets: true,
                }}
                modules={[EffectFade]}
                effect="fade"
                className={`mySwiper ${dark ? 'arrCursorDark' : 'arrCursorLight'}`}
                onInit={( ev ) => {
                    set_my_swiper( ev )
                }}
                onClick={() => my_swiper.slideNext()}
                loop={true}
            >
                {images.map( ( slideContent ) => (
                    <SwiperSlide key={slideContent}>
                        <img src={`../MainWorks/${slideContent}.jpg`} alt={slideContent} />
                    </SwiperSlide>
                ) )}
            </Swiper>
        </section>
    )
}

export default SideWorks
