import React from 'react'
// Import Swiper React components
import { Swiper, SwiperSlide, useSwiper } from 'swiper/react';
import { Pagination } from "swiper";
import { EffectFade } from 'swiper';

// Import Swiper styles
import 'swiper/css';
import "swiper/css/pagination";
import 'swiper/css/effect-fade';

const MainWorks = ( { title, category, images } ) => {
    const swiper = useSwiper();
    return (
        <section className='mainGalleryContainer'>
            <span className='mainWorksTitles'>
                <h1>{title}</h1>
                <h2>{category}</h2>
            </span>
            <Swiper
                pagination={{
                    dynamicBullets: true,
                }}
                modules={[Pagination, EffectFade]}
                effect="fade"
                className="mySwiper"
                onClick={() => swiper.slideNext()}
                loop={true}
            >
                {images.map( ( slideContent ) => (
                    <SwiperSlide key={slideContent}>
                        {/* <img src="./MainWorks/1.jpg" alt={slideContent} /> */}
                        <img src={`../MainWorks/${slideContent}.jpg`} alt={slideContent} />
                    </SwiperSlide>
                ) )}
            </Swiper>
        </section>
    )
}

export default MainWorks
