import React, { useEffect, useRef, Suspense, useCallback, useMemo, } from 'react'
import { gsap } from "gsap";
import { ScrollTrigger } from "gsap/ScrollTrigger";

import * as THREE from 'three';
import { OrbitControls } from 'three/examples/jsm/controls/OrbitControls';
import { Canvas, extend, useFrame, useLoader, useThree } from 'react-three-fiber';
import circleImg from '../assets/circle.png';
extend( { OrbitControls } )
gsap.registerPlugin( ScrollTrigger );

function CameraControls() {
    const {
        camera,
        gl: { domElement }
    } = useThree();

    const controlsRef = useRef();
    useFrame( () => controlsRef.current.update() )

    return (
        <orbitControls
            ref={controlsRef}
            args={[camera, domElement]}
            autoRotate
            autoRotateSpeed={-0.2}
        />
    );
}

function AnimationCanvas() {
    return (
        <Canvas
            colorManagement={false}
            camera={{ position: [100, 10, 0], fov: 75 }}
        >
            <Suspense fallback={null}>
                <Points />
            </Suspense>
            <CameraControls />
        </Canvas>
    );
}

function Points() {
    const imgTex = useLoader( THREE.TextureLoader, circleImg );
    const bufferRef = useRef();

    let t = 0;
    let f = 0.002;
    let a = 3;
    const graph = useCallback( ( x, z ) => {
        return Math.sin( f * ( x ** 2 + z ** 2 + t ) ) * a;
    }, [t, f, a] )

    const count = 100
    const sep = 3
    let positions = useMemo( () => {
        let positions = []

        for ( let xi = 0; xi < count; xi++ ) {
            for ( let zi = 0; zi < count; zi++ ) {
                let x = sep * ( xi - count / 2 );
                let z = sep * ( zi - count / 2 );
                let y = graph( x, z );
                positions.push( x, y, z );
            }
        }

        return new Float32Array( positions );
    }, [count, sep, graph] )

    useFrame( () => {
        t += 15

        const positions = bufferRef.current.array;

        let i = 0;
        for ( let xi = 0; xi < count; xi++ ) {
            for ( let zi = 0; zi < count; zi++ ) {
                let x = sep * ( xi - count / 2 );
                let z = sep * ( zi - count / 2 );

                positions[i + 1] = graph( x, z );
                i += 3;
            }
        }

        bufferRef.current.needsUpdate = true;
    } )

    return (
        <points>
            <bufferGeometry attach="geometry">
                <bufferAttribute
                    ref={bufferRef}
                    attachObject={['attributes', 'position']}
                    array={positions}
                    count={positions.length / 3}
                    itemSize={3}
                />
            </bufferGeometry>

            <pointsMaterial
                attach="material"
                map={imgTex}
                color={0x00AAFF}
                size={0.5}
                sizeAttenuation
                transparent={false}
                alphaTest={0.5}
                opacity={1.0}
            />
        </points>
    );
}


const Splash = () => {
    const headLineRef = useRef();

    useEffect( () => {
        gsap.fromTo(
            headLineRef.current,
            { autoAlpha: 0, y: -40 },
            {
                autoAlpha: 1, y: 0, duration: 1,
                scrollTrigger: {
                    scroller: 'html',
                    trigger: headLineRef.current,
                    start: "top 60%",
                    end: "bottom 0%",
                    toggleActions: "play none restart reverse"
                },
            },
        );
    }, [] );
    return (
        <div className='anim'>
            <Suspense fallback={<div>Loading...</div>}>
                <AnimationCanvas />
            </Suspense>
            <section className='splashContainer'>

                <h1 ref={headLineRef}>Raphaele G.</h1>
                <p>A game and web developer</p>
                <p>with a visual arts background</p>
                {/* <img className="toWorks" src="https://www.svgrepo.com/show/459330/down-arrow.svg" alt="" onClick={() => scrollTo( worksRef )} /> */}
            </section>
        </div>
    )
}

export default Splash
